import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Postcard from '../components/Postcard.jsx'
import axios from "axios";
import Navbar from "../components/Navbar.jsx";
import './communityforum.css';

const Container = styled.div`
    justify-content: space-between;
`;

const CommunityForum = () => {
    const [posts, setPosts] = useState([]);
    useEffect(() => {
        const fetchPosts = async () => {
          const res = await axios.get("/posts/all");
          setPosts(res.data);
        };
        fetchPosts();
      }, []);

    return (
        <>
        <header>
            <h1>FoodShare Network - Community Forum</h1>
            <span class="settings-icon" title="Settings">⚙️</span>
        </header>
        <div class="container">
            <h2>Community Blog Forum</h2>

            <div class="top-buttons">
                <button>All Posts</button>
                <button>Your Posts</button>
                <button>Create New Post</button>
            </div>

            <div class="post-card">
                <Container>
                    {posts.map(post=>(
                        <Postcard key={post._id} post = {post}/>
                    ))}
                </Container>
            </div>

            <div class="post-card">
                <p class="post-title">Community Pantry Adds Fresh Produce</p>
                <p class="post-meta">Posted by GreenGrocery • 1 day ago</p>
            </div>

            <div class="post-card">
                <p class="post-title">Tips for First-Time Volunteers</p>
                <p class="post-meta">Posted by Liam • 3 days ago</p>
            </div>

            <p class="footer-text">New posts appear from newest to oldest.</p>
        </div>
        </>
    )
};

export default CommunityForum;
