import './community-create-post.css';
// have to add back the cancel post function
import styled from "styled-components";

const Form = styled.form`
    display: flex;
    flex-direction: column;
    width: 180%;
`


const CreatePost = () => {


return (
    <>
        <head>
            <meta charset="UTF-8" />
            <meta name="viewport" content="width=device-width, initial-scale=1.0" />
            <title>Create Blog Post | FoodShare Network</title>
        </head>
            <header>
                <h1>FoodShare Network - Create Blog Post</h1>
                <span class="settings-icon" title="Settings">⚙️</span>
            </header>
            <div class="container">
                <Form>
                <h2>Create a New Blog Post</h2>
                    <label for="title">Blog Title</label>
                    <input type="text" id="title" name="title" placeholder="Enter your blog title..." required />

                    <label for="content">Blog Content</label>
                     <textarea id="content" name="content" placeholder="Write your post here..." required></textarea>

                    <label for="mediaUpload">Upload Photo or Video (optional)</label>
                    <input type="file" id="mediaUpload" name="mediaUpload" accept="image/*,video/*" />

                    <div class="button-group">
                        <button type="submit" class="publish-btn">Publish</button>
                        <button type="button" class="cancel-btn" onclick="cancelPost()">Cancel</button>
                    </div>
                </Form>
            </div>
    </>

 );
}
export default CreatePost;
