import './provider-landing.css';
import { useSelector } from 'react-redux';
import { Link, useNavigate } from "react-router-dom";
import { logout } from "../redux/userSlice";
import axios from "axios";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import Navbar from './Navbar.jsx';


const User = styled.div`
  display: flex;
  align-item: center;
  gap: 10px;
  font-weight: 500;
`
const Avatar = styled.img`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background-color: #999;
`


const ProviderLanding = () => {

  const {currentUser} = useSelector(state=>state.user);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleDelete = async () => {
      await axios.delete(`/providers/${currentUser._id}`);
      // delete their posts as well?
      dispatch(logout(currentUser._id));
     }

  const handleLogout = async () => {
        dispatch(logout()) && navigate(`/`);
     }


 return (
   <>
        <head>
            <meta charset="UTF-8" />
            <meta name="viewport" content="width=device-width, initial-scale=1.0" />
            <title>Landing Page - Provider | FoodShare Network</title>
        </head>
        <header>
          <h1>FoodShare Network - Provider Dashboard</h1>
          {currentUser ? (
            <User>
              <Avatar/>
              {currentUser.username}
            </User>
             ) : (
             <Link to ="/" style={{ textDecoration: "none"}}>
            <button>
              SIGN IN
            </button>
          </Link>)}
          <span class="settings-icon" title="User Settings">⚙️</span>
          <button onClick={handleDelete}>DELETE ACCOUNT</button>
          <button onClick={handleLogout}>SIGN OUT</button>
        </header>

        <div class="container">
            <div class="card">
                <h2>Add to Inventory</h2>
                <p>Update your available stock.</p>
                <button>Manage Inventory</button>
            </div>

            <div class="card">
                <h2>Notification Configuration</h2>
                <p>Send alerts to subscribers for sales or restocks.</p>
                <button >Configure Notifications</button>
            </div>

            <div class="card">
                <h2>View Current Storefront</h2>
                <p>Preview your store as users see it and check your current offerings.</p>
                <button >View Storefront</button>
            </div>

            <div class="card">
                <h2>Profile Settings</h2>
                <p>Update your organization’s information, contact details, and preferences.</p>
                <Link to ="/editproviderprofile">
                    <button >Edit Profile</button>
                </Link>
            </div>
        </div>
   </>

 );
}
export default ProviderLanding;
