import './Navbar.css';
import { Link, useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { useSelector } from 'react-redux';
import { logout } from "../redux/userSlice";
import axios from "axios";
import styled from "styled-components";


const StyledHeader = styled.header`
    padding: 10px 0;

`


const StyledNav = styled.nav`
    display: flex;
    align-items: center;
    justify-content: left;
    padding: 10px 0;
` 

const NavContainer = styled.div`
    width: 1000px;
    max-width: 100%;
    padding: 0 20px;
    margin: 0 auto;
`

const User = styled.div`
  display: flex;
  align-item: center;
  gap: 10px;
  font-weight: 500;
`
const Avatar = styled.img`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background-color: #999;
`

const Navbar = () => {
    const {currentUser} = useSelector(state=>state.user);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleDelete = async () => {
      await axios.delete(`/students/${currentUser._id}`);
      // delete their posts as well?
      dispatch(logout(currentUser._id));
     }

    const handleLogout = async () => {
        dispatch(logout()) && navigate(`/`);
     }
    return (
        <>
        <StyledHeader>
            <header>
                <h1>FoodShare Network</h1>
                {currentUser ? (
                    <User>
                    <Avatar/>
                    {currentUser.username}
                    </User>
                    ) : (
                    <Link to ="/" style={{ textDecoration: "none"}}>
                    <button>
                    SIGN IN
                    </button>
                </Link>)}
                <span class="settings-icon" title="User Settings">⚙️</span>
                <button onClick={handleDelete}>DELETE ACCOUNT</button>
                <button onClick={handleLogout}>SIGN OUT</button>
            </header>
            <nav>
                <Link to="/userlanding">Home</Link>
                <Link to="/userlanding">Find Providers</Link>
                <Link to="/userlanding">Order History</Link>
                <Link to="/userlanding">Current Activity</Link>
                <Link to="/communityhome">Community</Link>
            </nav>
        </StyledHeader>
        </> 
    );
};

export default Navbar;
